package controler;

public class Player {
	
	private String nom;
	private int score;
	
	public Player() {
		// TODO Auto-generated constructor stub
	}
	
	public void setNom(String nom) {
		this.nom = nom;
	}
	
	public String getNom() {
		return this.nom;
	}
	
	public void setScore(int score) {
		this.score = score;
	}
	public int getScore() {
		return this.score;
	}
	
}
